import React from 'react';

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      news: []
    }
  }
  componentDidMount() {
    fetch('https://cryptic-ravine-96718.herokuapp.com/')
  .then((response) => {
    return response.json();
  })
  .then((myJson) => {
    this.setState({
      news: myJson.news
    })
  });
  }
  render() {
    console.log(this.state)
    return (
      <div>
       {this.state.news.map((item, index) => {
         return (
           <h2 key={item}>
             {item.title}
           </h2>
         )
       })}
      </div>
    )
  }
}

export default App;
